import React, { useState} from "react";


const Tasklist=()=>{

    const [task,setTasks] = useState("");
    const [items,setItems]=useState([]);


  function passData(val)
  {
  setTasks(val)
alert(task)
  }

  const addItem=()=>{
    if(!task){
    }else{
    setItems([task,...items]);

    setTasks("")
 }
 }



    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">
                          <label class="heading"> ToDo App</label> <br></br> 
                        <span class="thirdcontainer">
                          <input type="text" class="todonametxt" autoComplete="off" maxLength="20" onChange={(e)=>passData(e.target.value)}></input>&nbsp;&nbsp;
                          </span>
                          <span class="imgbutton"><button onClick={addItem}><img class="plusimage" src="/image/plus.png"></img></button></span>

                          { 

    items.map((elm,ind)=>{

        return(

                <div key={ind}>

                <input type="radio"  name="taskname"  onChange={(e)=>{

                setItems(items.filter(obj2=>{


                    if(obj2.id===elm.id){
            {
                        obj2.status=e.target.checked



                //  alert("obj2="+obj2.status)


                  updateTaskStatus(obj2.text,obj2.status)
            }

                    }
                    return obj2

                })


                    )





                }}value={elm.status} checked={elm.status} class="chkbox" name="Checkbox1"/>

         <label style={{textDecoration:elm.status?"line-through":"none"} } name="checktext">{elm.text}</label>

        <i className="far fa-trash-alt add-btn" title="delete Item" onClick={()=>deleteItem(ind,elm.text)} ></i><br></br>

              </div>

             ) 

            }





            )

}






                          </div>

                   </div>


           </div>


          </react.Fragment>



          )

}
export default Tasklist;
