    name=e.target.name;
    value=e.target.value;
    setUser({...user,[name]:value});
  }

  const registerData=()=>{
    const {fname,lname,email,password,cpassword}=user;

    if(user){

      Axios.post("http://localhost:4000/signupinsert",{

      fname,lname,email,password,cpassword

