    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">

                          &nbsp;&nbsp;&nbsp;<label class="heading">SignUp</label> <br></br> <br></br>

                         <form onSubmit={handleSubmit(submitForm)}>

                        <span class="thirdcontainer">
                        <img  class="userpic" src="/image/usericon.jpeg"></img>&nbsp;
                          <input type="text"  name="fname" value={user.fname} class="todonametxt" placeholder="First Name....." ref={register}   autoComplete="nope" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span><br></br>

                          <span class="thirdcontainer">
                             <img  class="userpic" src="/image/usericon.jpeg"></img>&nbsp;

                          <input type="text"  name="lname" value={user.lname} class="todonametxt" placeholder="Second Name....." ref={register}     autoComplete="nope" maxLength="20" onChange={handleInputs} ></input><br></br>
                          </span>

                          <span class="thirdcontainer">
                          <img  class="mailpic" src="/image/mails.jpeg"></img>&nbsp;
                          <input type="text" name="email" value={user.email}  class="todonametxt" placeholder="E_mail....." ref={register} autoComplete="nope" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span><br></br>



                        <span class="thirdcontainer">
                        <img  class="mailpic" src="/image/lock.jpeg"></img>&nbsp;
                          <input type="text"  name="password" value={user.password} class="todonametxt" placeholder="Password....." ref={register} autoComplete="nope" maxLength="20" onChange={handleInputs}></input>
                          </span><br></br>


                          <span class="thirdcontainer">
                          <img  class="mailpic" src="/image/lock.jpeg"></img>&nbsp;
                            <input type="text"  name="cpassword" value={user.cpassword} class="todonametxt" placeholder="Re_enetr Password....." ref={register}    autoComplete="nope" maxLength="20" onChange={handleInputs} ></input>&nbsp;&nbsp;
                            </span>
                          <div class="btnclass">
                          <button  class="todobtn" onClick={registerData}>Register</button>&nbsp;&nbsp;<button class="todobtn" onClick={cancelData }>Reset</button>
                          </div>


                    </form>

                          </div>


                   </div>



           </div>



          </react.Fragment>



          )

}
export default SignUp;
