import react from 'react';
import React, { useState} from "react";


const Tasklist=()=>{

    const [task,setTasks] = useState("");
    const [items,setItems]=useState([]);


  function passData(val)
  {
  setTasks(val)

  }


  const deleteItem=(id,task_del)=>{
    const updatedItems=items.filter((elm,ind)=>{

        return ind!==id;

                     } ) ;
             //deleteTask(task_del);
             setItems(updatedItems);

        } 

  const addItem=()=>{
    if(!task){
    }else{
    setItems([{id:Date.now(),text:task,status:false},...items]);

    setTasks("")
 }
 }
const editItem=()=>{


}



    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">
                          <label class="heading"> ToDo App</label> <br></br> 

                        <span class="thirdcontainer">
                          <input type="text" class="todonametxt" value={task}autoComplete="off" maxLength="20" onChange={(e)=>passData(e.target.value)}></input>&nbsp;&nbsp;
                          </span>
                          <span class="imgbutton"><button onClick={addItem}><img class="plusimage" src="/image/plus.png"></img></button></span>

                          { 

    items.map((elm,ind)=>{

        return(

                <div class="hhh" key={ind}>

                <input type="radio"  name="taskname"     value={elm.status} checked={elm.status} class="optionbtn" name="option"/>                 




         <label name="checktext">{elm.text}</label>
         <img title="edit item" class="editimg" src="https://img.icons8.com/color/48/000000/edit-property.png" onClick={()=>editItem(ind)}/>
         <i class="fa fa-trash" title="delete Item" onClick={()=>deleteItem(ind,elm.text)}></i>


              </div>


             ) 

            }





            )

}





                          </div>

                   </div>


           </div>


          </react.Fragment>



          )

}
export default Tasklist;
