import react from 'react';
import React, { useState} from "react";


const Tasklist=()=>{

    const [task,setTasks] = useState("");
    const [items,setItems]=useState([]);


  function passData(val)
  {
  setTasks(val)

  }

  const addItem=()=>{
    if(!task){
    }else{
    setItems([{id:Date.now(),text:task,status:false},...items]);

    setTasks("")
 }
 }




    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">
                          <label class="heading"> ToDo App</label> <br></br> 

                        <span class="thirdcontainer">
                          <input type="text" class="todonametxt" value={task}autoComplete="off" maxLength="20" onChange={(e)=>passData(e.target.value)}></input>&nbsp;&nbsp;
                          </span>
                          <span class="imgbutton"><button onClick={addItem}><img class="plusimage" src="/image/plus.png"></img></button></span>

                          { 

    items.map((elm,ind)=>{

        return(

                <div class="hhh" key={ind}>

                <input type="radio"  name="taskname"     value={elm.status} checked={elm.status} class="optionbtn" name="option"/>                 




                <label style={{textDecoration:elm.status?"line-through":"none"} } name="checktext">{elm.text}</label>

<i className="far fa-trash-alt add-btn" title="delete Item" onClick={()=>deleteItem(ind,elm.text)} ></i><br></br>




              </div>

             ) 

            }





            )

}





                          </div>

                   </div>


           </div>


          </react.Fragment>



          )

}
export default Tasklist;
