import react from 'react';
import React, { useState} from "react";
import {useHistory} from "react-router-dom";
import{useContext} from 'react';
import {ListContext} from '../context/ListContext.js'

const Tasklist=()=>{
  const{optionname,setOptionname}=useContext(ListContext)
    const [task,setTasks] = useState("");
    const [items,setItems]=useState([]);
    const[toggleBtn,setToggleBtn]=useState(true);
    const[isEditItem,setIsEditItem]=useState(null);
    const[optStatus,setOptStatus]=useState(false)
    let history=useHistory();

  function passData(val)
  {
  setTasks(val)

  }


  const deleteItem=(id,task_del)=>{
    const updatedItems=items.filter((elm,ind)=>{

        return ind!==id;

                     } ) ;
             //deleteTask(task_del);
             setItems(updatedItems);

        } 

  const addItem=()=>{
    if(!task){
    }else if(task && !toggleBtn){

      setItems(
        items.map((elem)=>{
          if(elem.id===isEditItem){
            setToggleBtn(true)

            setTasks(" ")
            return{...elem,text:task}

          }
          return elem;
        })

      )
    } else{
    setItems([{id:Date.now(),text:task,status:false},...items]);

    setTasks("")

 }
 }
const editItem=(id)=>{

  let newEditItem=items.find((elem)=>{
    alert("elem="+elem.id)
    alert("id="+id)
return elem.id===id

  });
  setToggleBtn(false);
setTasks(newEditItem.text)
setIsEditItem(id)


}
const submitTask=()=>{



  history.push("/childlist");



}


    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">
                          <label class="heading"> ToDo App</label> <br></br> 

                        <span class="thirdcontainer">
                          <input type="text" class="todonametxt" value={task.toUpperCase}autoComplete="off" maxLength="20" onChange={(e)=>passData(e.target.value)}></input>&nbsp;&nbsp;
                          </span>
                          <span class="imgbutton">
                            {

                              toggleBtn?<button onClick={addItem}><img class="plusimage" title="delete" src="/image/plus.png"></img></button>:<button onClick={addItem}><img class="plusimage" title="edit" src="https://img.icons8.com/color/48/000000/edit-property.png"></img></button>
                            }
                            </span>

                          { 

    items.map((elm,ind)=>{

        return(


                <div class="hhh" key={ind}>

                <input type="radio"  name="taskname"    onChange={(e)=>{

                 setItems(items.filter(obj2=>{


                  if(obj2.id===elm.id){

                   obj2.status=e.target.checked

  setOptionname(elm.text);
  alert(optionname)
              // alert("obj2="+obj2.status)


              //  updateTaskStatus(obj2.text,obj2.status)


                  }
                  return obj2


                 })
                 )}

                 } value={elm.text} checked={elm.status} class="optionbtn" name="option" onClick= {submitTask} 

                 />




         <label name="text">{elm.text}</label>
         <img title="edit item" class="editimg" src="https://img.icons8.com/color/48/000000/edit-property.png" onClick={()=>editItem(elm.id)}/>
         <i class="fa fa-trash" title="delete Item" onClick={()=>deleteItem(ind,elm.text)}></i>


              </div>


             ) 

            }





            )

}





                          </div>

                   </div>


           </div>


          </react.Fragment>



          )

}
export default Tasklist;
