import react from 'react';
import React, { useState} from "react";

const SignUp=()=>{

  const [user,setUser] = useState({

fname:"",lname:"",email:"",password:"",cpassword:""

  });

  let name,value;
  const handleInputs=(e)=>{

    name=e.target.name;
    value=e.target.value;
    setUser({...user,[name]:value});
  }




  function passData(val,name)

  {
    alert(name)
  }

    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">

                          &nbsp;&nbsp;&nbsp;<label class="heading">SignUp</label> <br></br> <br></br>
                          <label class="todolbl" >Firstname</label>

                        <span class="thirdcontainer">
                          <input type="text"  name="fname" value={e.target.fname} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span><br></br>
                          <label class="todolbl" >Last Name</label><br></br>
                          <span class="thirdcontainer">
                          <input type="text"  name="lname" value={e.target.lname} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs} ></input><br></br>
                          </span>
                          <label class="todolbl" >Email address</label><br></br>
                          <span class="thirdcontainer">
                          <input type="text" name="email" value={e.target.email}  class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span>

                          <label class="todolbl" >Password</label>

                        <span class="thirdcontainer">
                          <input type="text"  name="password" value={e.target.password} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs}></input>
                          </span><br></br>
                          <label class="todolbl" >Re-enter Password</label>

                          <span class="thirdcontainer">
                            <input type="text"  name="cpassword" value={e.cpassword} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs} ></input>&nbsp;&nbsp;
                            </span>
                          <div class="btnclass">
                          <button  class="todobtn">Register</button>&nbsp;&nbsp;<button class="todobtn">Reset</button>
                          </div>




                          </div>


                   </div>



           </div>



          </react.Fragment>



          )

}
export default SignUp;
