import react from 'react';
import React, { useState} from "react";
import Axios from 'axios';
const SignUp=()=>{

  const [user,setUser] = useState({

fname:"",lname:"",email:"",password:"",cpassword:""

  });

  let name,value;
  const handleInputs=(e)=>{

    name=e.target.name;
    value=e.target.value;
    setUser({...user,[name]:value});
  }

  const registerData=()=>{
    const {fname,lname,email,password,cpassword}=user;

    if(user){

      Axios.post("http://localhost:4000/signupinsert",{

      fname,lname,email,password,cpassword

      }).then(()=>{
        alert("successful insert")});


                  }
       };  



  function passData(val,name)

  {
    alert(name)
  }

    return(

        <react.Fragment>

          <div class="parent_div">
                   <div class="container">


                          <div class="secondcontainer">

                          &nbsp;&nbsp;&nbsp;<label class="heading">SignUp</label> <br></br> <br></br>
                          <form>  


                        <span class="thirdcontainer">
                          <input type="text"  name="fname" value={user.fname} class="todonametxt" placeholder="First Name....." autoComplete="off" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span><br></br>
                          <label class="todolbl" >Last Name</label><br></br>
                          <span class="thirdcontainer">
                          <input type="text"  name="lname" value={user.lname} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs} ></input><br></br>
                          </span>
                          <label class="todolbl" >Email address</label><br></br>
                          <span class="thirdcontainer">
                          <input type="text" name="email" value={user.email}  class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs}></input>&nbsp;&nbsp;
                          </span>

                          <label class="todolbl" >Password</label>

                        <span class="thirdcontainer">
                          <input type="text"  name="password" value={user.password} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs}></input>
                          </span><br></br>
                          <label class="todolbl" >Re-enter Password</label>

                          <span class="thirdcontainer">
                            <input type="text"  name="cpassword" value={user.cpassword} class="todonametxt"  autoComplete="off" maxLength="20" onChange={handleInputs} ></input>&nbsp;&nbsp;
                            </span>
                          <div class="btnclass">
                          <button  class="todobtn" onClick={registerData}>Register</button>&nbsp;&nbsp;<button class="todobtn">Reset</button>
                          </div>

                          </form>


                          </div>


                   </div>



           </div>



          </react.Fragment>



          )

}
export default SignUp;
